INSERT INTO `novels` (`id`, `title`, `description`, `Count`, `cover_image`, `created_at`, `status`, `author`, `userid`, `uploadby`) VALUES
(1, 'การเกิดใหม่ของอังกฤษ', '“เพื่อรักษาการพัฒนาที่หลากหลายของอังกฤษ ฉันเชื่อว่าควรมีการตรวจสอบพฤติกรรมผูกขาดใน อุตสาหกรรมบางประเภทอย่างละเอียด ผู้ผูกขาด รวมถึงตระกูลเดวอนเชียร์ จำเป็นต้องเปิดพื้นที่ให้มากขึ้นสำหรับการแข่งขันเต็มรูปแบบในตลาด!”\n\nในปี 2022 ในสุนทรพจน์เปิดตัวต่อรัฐสภา คำกล่าวของนายกรัฐมนตรีหญิงคนใหม่ทำให้คนทั้งสถานที่เงียบงัน\n\nหนึ่งเดือนต่อมา นายกรัฐมนตรีถูกฟ้องร้องและปลดออกจากตำแหน่งเพราะปกป้องผู้ใต้บังคับบัญชาของเธอ\n\nชาวอังกฤษทุกคนไม่อาจแยกจากครอบครัวเดวอนเชียร์ได้ตั้งแต่เกิดจนตาย\n\nและตำนานของตระกูลนี้ก็เริ่มโด่งดังเกินอดีตนับตั้งแต่วันนั้นในปี 2002\n\nปีนั้นเอง บารอน คาเวนดิช ได้สืบทอดตำแหน่งดยุค', 11, '/uploads/88746s.jpg', '2025-04-13 21:49:48', 'ยังไม่จบ', 'admin', 'cefe48d', 'admin'),
(2, 'การเติบโตของออสเตรเลีย', '<br>', 1, '/uploads/cover-1744814684434-261493571.jpg', '2025-04-16 14:44:44', 'ยังไม่จบ', 'admin', 'cefe48d', 'admin'),
(17, 'Cosmic Vision Club ปลดล็อกมิติข้ามจักรวาลป่วนคูณสอง', 'พบกับการผจญภัยสู่โลกเหนือจินตนาการ การต่อสู้ในภารกิจสุดล้ำ ของ‘สกายเลอร์’แฮกเกอร์หนุ่มผู้มี‘บั๊ก’ซะเองและสองสาวสฺดป่วนจากต่างมิติ พวกเขาจะทำอย่างไรกับหายนะที่ซ่อนเร้น ที่เปลี่ยนทุกสิ่งไปตลอดกาล', 2, '/uploads/image-1745764317427-361763937.png', '2025-04-27 14:31:57', 'ยังไม่จบ', 'plutonium', 'cefe48d', 'admin'),
(23, 'kh\'n', 'jgf', 0, '/uploads/image-1746509636783-119093618.jpg', '2025-05-06 05:33:57', 'ยังไม่จบ', 'mb/ j', 'cefe48d', 'admin');

